select pc.Seriale,m.data_consegna,m.cavo_rete,m.alimentatore,m.borsa,m.mouse,m.hdd,m.con_ethernet,m.con_usb,m.note,m.note_movimento,utente.nome,utente.cognome,c.corso from movimento as m inner join pc on m.PC_idpc=pc.idpc join utente on m.UTENTE_idUTENTE=utente.idUTENTE inner join CORSO as c on utente.CORSO_idCORSO=c.idCORSO   inner join sede as s on c.SEDE_idSEDE=s.idSEDE inner join STATO on pc.STATO_idSTATO=STATO.idSTATO where  m.ADMIN_idADMIN=1  order by   m.idMOVIMENTO desc 
